Item.method_code:
// 追加されたリレーションアイテムを削除
try{
gridApplet.DeleteRow(relationshipID);
}catch(e){
	alert(e);
}
var node = parent.item.selectSingleNode("//Item[@id='" + relationshipID + "']");
if (node)
{
  node.parentNode.removeChild(node);
}

// 追加できない旨のメッセージを表示
top.aras.AlertError("It is not possible to add the line");

// 戻り値を返却
return false;
